import { useEffect, useState } from "react";

import Barang from './Barang'

const Content = () => {

  const [barang, setBarang] = useState([]);

  useEffect(() => {
    // console.log("ok2");
    // memanggil API untuk mengambil data barang
    fetch("http://127.0.0.1:8000/api/barang")
      .then((res) => {
        // console.log(res.json);
        return res.json();
      })
      .then((data) => {
        // ketika Rest API sukses, simpan data dari response ke dalam state lokal
        setBarang(data);
        // console.log(data);
      })
      .catch((err) => {
        if (err.name === "AbortError") {
          console.log("fetch aborted.");
        }
      });
  }, []);

  // const handleClick=() =>{
    const filter = () => {
      console.log("ok");
    // fetch('http://localhost:8000/api/get?filter=' + this.state.filter)
    // .then(response => response.json())
    // .then((json) => {
    //   this.setState({
    //     provinces: json.data
    //   })
    // })
  }

  return (
    // <ul id="barang-list">
    <div class="album py-5 bg-light">
      <div class="container">

        <div class="row " style={{paddingLeft:35+"%"}}>

          <input type="text" class="form-control mb-2" value={this.state.filter}  id="inlineFormInputName2" placeholder="Filter Barang" style={{width:30+"%", marginRight:15+"px"}} />
          <button type="submit" onClick={filter} class="btn btn-primary mb-2 " style={{width:10+"%"}} >Submit</button>

        </div>

        <div class="row row-cols-1 row-cols-sm-2 row-cols-md-3 g-3">
          {barang.map((barang1) => (
            <Barang barang={barang1} key={barang1.id} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default Content;
