import { Link } from "react-router-dom";
const Barang = ({ barang }) => {
  // console.log(barang);
  return (
    // <div> {barang.nama_barang} <span class="close">x</span></div>

    <div class="col">
      <div class="card shadow-sm">

        <img src={"http://127.0.0.1:8000/images/" + barang.url} alt="" />
        <div class="card-body">
          <p class="card-text">{barang.nama_barang}</p>
          {/* <p class="card-text">16GB</p> */}
          {/* <p class="card-text">3D Touch. 12MP photos. 4K video.</p> */}
          <p class="card-text">{new Intl.NumberFormat("id-ID", { style: "currency", currency: "IDR" }).format(barang.harga)}</p>
          <div class="d-flex justify-content-between align-items-center">
            <div class="btn-group">
              <Link 
                to={{
                  pathname: "/barang/1",
                  state: {
                    name: "Pevita Cleo Eileen Pearce",
                    born: "1992-10-06"
                  }
                }}>
                <button type="button" class="btn btn-sm btn-outline-secondary">Detail</button>
              </Link>
            </div>
            {/* <small class="text-muted">9 mins</small> */}

          </div>
        </div>
      </div>
    </div>

  );
}

export default Barang;
