import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
// import 'bootstrap/dist/css/bootstrap.min.css';
// import 'bootstrap/dist/js/bootstrap.min.js';
import './Keranjang.css';


const Keranjang = () => {
    let obj_data = JSON.parse(localStorage.getItem('Keranjang'));
    // console.log(obj_data.data.nama_barang);
    // console.log();
    {/* {obj_data.map(function (key, index) {
                // console.log(key.id); 
                "ok"
    })}  */}

    return (
        <div class="container">
            <div class="card shopping-cart">
                <div class="card-header bg-dark text-light">
                    <i class="fa fa-shopping-cart" aria-hidden="true"></i>
                Shipping cart
                <a href="" class="btn btn-outline-info btn-sm pull-right">Continiu shopping</a>
                    <div class="clearfix"></div>
                </div>
                <div class="card-body">

                    {obj_data.map((key, index) => (
                        <>
                            <div class="row">
                                <div class="col-12 col-sm-12 col-md-2 text-center">
                                    {/* <img class="img-responsive" src="http://placehold.it/120x80" alt="prewiew" width="120" height="80" /> */}
                                    <img class="img-responsive" src={"http://127.0.0.1:8000/images/" + key.data.url} alt="prewiew" width="120" height="80" />

                                </div>
                                <div class="col-12 text-sm-center col-sm-12 text-md-left col-md-6">
                                    {/* <h4 class="product-name"><strong>Product Name</strong></h4> */}
                                    <h4 class="product-name"><strong>{key.data.nama_barang}</strong></h4>
                                    <h4 class="product-name">
                                        <small>Product description</small>
                                    </h4>
                                </div>
                                <div class="col-12 col-sm-12 text-sm-center col-md-4 text-md-right row">
                                    <div class="col-3 col-sm-3 col-md-6 text-md-right" style={{ paddingTop: 5 + 'px' }}>
                                        <h6><strong>{new Intl.NumberFormat("id-ID", { style: "currency", currency: "IDR" }).format(key.data.harga)} <span class="text-muted">x</span></strong></h6>

                                    </div>
                                    <div class="col-4 col-sm-4 col-md-4">
                                        <div class="quantity">
                                            {/* <input type="button" value="+" class="plus" /> */}
                                            <input type="number" step="1" max="99" min="1"  title="Qty" class="qty"
                                                size="4" />

                                            {/* <input type="button" value="-" class="minus" /> */}
                                        </div>
                                    </div>
                                    {/* <div class="col-2 col-sm-2 col-md-2 text-right">
                                        <button type="button" class="btn btn-outline-danger btn-xs">
                                            <i class="fa fa-trash" aria-hidden="true"></i>
                                        </button>
                                    </div> */}
                                </div>
                            </div>

                            <hr />
                        </>
                    ))}


                    {/* <div class="pull-right">
                        <a href="" class="btn btn-outline-secondary pull-right">
                            Update shopping cart
                        </a>
                    </div> */}
                </div>
                <div class="card-footer">
                    {/* <div class="coupon col-md-5 col-sm-5 no-padding-left pull-left">
                        <div class="row">
                            <div class="col-6">
                                <input type="text" class="form-control" placeholder="cupone code" />
                            </div>
                            <div class="col-6">
                                <input type="submit" class="btn btn-default" value="Use cupone" />
                            </div>
                        </div>
                    </div> */}
                    <div class="float-right" style={{ margin: 10 + 'px' }}>
                        <a href="" class="btn btn-success pull-right">Checkout</a>
                        <div class="pull-right" style={{ margin: 5 + 'px' }}>
                            Total price: <b>50.00â‚¬</b>
                        </div>
                    </div>
                </div>
            </div>
        </div>




    );
}

export default Keranjang;
