import { useEffect, useState } from "react";
import { Link,useParams } from "react-router-dom";
import './Detail.css'

const Detail  = () => { 
  const { id } = useParams();
  // console.log(id);

  const [barang, setBarang] = useState([]);

  useEffect(() => {
    // console.log("ok2");
    // memanggil API untuk mengambil data barang
    fetch("http://127.0.0.1:8000/api/barang/1")
      .then((res) => {
        // console.log(res.json);
        return res.json();
      })
      .then((data) => {
        // ketika Rest API sukses, simpan data dari response ke dalam state lokal
        setBarang(data);
        // console.log(data);
      })
      .catch((err) => {
        if (err.name === "AbortError") {
          console.log("fetch aborted.");
        }
      });
  }, []);
  // console.log(localStorage.getItem('User'));

  const handleClick=() =>{
    // console.log(JSON.stringify(barang));
    // console.log(JSON.parse(localStorage.getItem('Keranjang')));
    let data1 = JSON.parse(localStorage.getItem('Keranjang'));
    let data2 = [];
    data2 = 
    { id : barang.id_barang,
      jumlah:1,
      data: barang
    };
    // let data2 = 
    // { id : 2,
    //   jumlah:1,
    //   data: barang
    // };
    // let data2 = JSON.parse(localStorage.getItem('Keranjang'));
    // data1.push(data2);

    // console.log(data1);
    localStorage.setItem('Keranjang', JSON.stringify(data1));
    // localStorage.setItem('Keranjang', JSON.stringify(barang));
    // console.log(localStorage.getItem('Keranjang'));
    // let obj_data=JSON.parse(localStorage.getItem('Keranjang'));
    // console.log(obj_data);
  }

  return (

    <div class="container container_detail">

      <div class="row mb-2">
        <div class="col-md-12">
          <div class="row g-0 border rounded overflow-hidden flex-md-row mb-4 shadow-sm h-md-250 position-relative">
            <div class="col-auto d-none d-lg-block col-img" >
              <img src={"http://127.0.0.1:8000/images/product-iphone-6s-plus.png"} alt="" />

            </div>
            <div class="col p-4 d-flex flex-column position-static">
              <div class="row line-bott" >
                {/* <h5>Apple iPhone 6S Plus (16GB) Silver - #89270182</h5> */}
                <h5>{barang.nama_barang}</h5>
              </div>
              <div class="row line-bott" >
                <ul>
                  <li>5.5" Retina HD Display with 3D Touch</li>
                  <li>Fingerprint-resistant oleophobic coating</li>
                  <li>A9 chip with 64-bit</li>
                  <li>Ultrafast 4G LTE Advanced wireless</li>
                  <li>New 12-megapixel iSight camera</li>
                </ul>
              </div>
              <div class="text-center">
                <Link 
                to=
                {{
                  pathname: "/keranjang",
                }}>
                <button onClick={handleClick} type="button" class="button-beli btn btn-secondary btn-lg">Tambah Ke Keranjang</button>
              </Link>                
              </div>
            </div>

          </div>
        </div>
      </div>
    </div>
  );
}

